function validateForm(e){e.validate({ignore:".ignore",rules:{first_name:{required:!0,maxlength:150},last_name:{required:!0,maxlength:150},email:{required:!0,email:!0},mobile:{phone_number:!0},fax:{maxlength:150},job_name:{maxlength:150},company:{maxlength:150},message:{maxlength:1500},agree:{required:!0}},messages:{first_name:{required:"nhập first name",maxlength:"first name nhỏ hơn 150 "},last_name:{required:"nhập last_name",maxlength:"last_name nhỏ hơn 150 "},email:{required:"nhập email bạn ơi",email:"email không đúng bạn"},mobile:{phone_number:"phone không đúng bạn"},fax:{maxlength:"nhỏ hơn 150"},job_name:{maxlength:"nhỏ hơn 150"},company:{maxlength:"nhỏ hơn 150"},message:{maxlength:"nhỏ hơn 1500"},agree:{required:"bấm vô đây bạn"}}})}jQuery.validator.addMethod("phone_number",function(e,n){return-1==e.indexOf("--")&&(-1==e.indexOf("++")&&(e.length>8&&e.match(/^[+]*[(]{0,1}[0-9]{1,4}[)]{0,1}[-\s\./0-9]*$/)))}),$(document).ready(function(){var e=$(".js-validate-form");e.length&&validateForm(e)});